mapper.select:
select

from t_user
where user_name = #{username,jdbcType=VARCHAR}
limit 1;

SELECT

FROM t_user


        user_name = #{username}


        AND user_pass = #{password}


        AND user_role = #{role}


        AND user_level = #{level}


        AND user_mobile = #{mobile}


        AND user_email = #{email}


        AND user_real_name = #{realName}


        AND user_create_time = #{createTime}

SELECT

FROM t_user


        user_id = #{id}


        user_name = #{username}


        AND user_pass = #{password}


        AND user_role = #{role}


        AND user_level = #{level}


        AND user_mobile = #{mobile}


        AND user_email = #{email}


        AND user_real_name = #{realName}


        AND user_create_time = #{createTime}
